<div class="container">    <form  method="post" action="/admin/edituser/<?=$user->USER_ID ?>">        <!--        ADDRESS_ID FULL_NAME USER_NAME EMAIL BIRTHDAY PHONE_NUMBER-->        <div class="form-group">            <label for="FULL_NAME"> FULL NAME </label>            <input type="text" class="form-control" value="<?= $user->FULL_NAME ?>" name="FULL_NAME"                   id="FULL_NAME" required>        </div>        <div class="form-group">            <label for="USER_NAME"> USER_NAME </label>            <input type="text" class="form-control" value="<?= $user->USER_NAME ?>" name="USER_NAME" id="USER_NAME" required >        </div>        <div class="form-group">            <label for="EMAIL"> EMAIL </label>            <input type="email" class="form-control" value="<?= $user->EMAIL ?>" name="EMAIL"                   id="EMAIL" required>        </div>        <div class="form-group">            <label for="PASSWORD"> PASSWORD </label>            <input type="PASSWORD" class="form-control" value="" name="PASSWORD"                   id="PASSWORD" >        </div>        <div class="form-group">            <label for="exampleFormControlSelect1"> select Type </label>            <select name="TYPE" class="form-control">                <option <?= $this->selectedIf('TYPE_ID' , 3 , $user) ?> value="3"> Student </option>                <option <?= $this->selectedIf('TYPE_ID' , 2 , $user) ?> value="2"> Instractor </option>                <option <?= $this->selectedIf('TYPE_ID' , 1 , $user) ?> value="1"> Admin </option>            </select>        </div>        <div class="form-group">            <label for="PHONE_NUMBER"> PHONE_NUMBER </label>            <input type="text" class="form-control" value="<?= $user->PHONE_NUMBER ?>" name="PHONE_NUMBER" id="PHONE_NUMBER" required >        </div>        <div class="form-group">            <label for="BIRTHDAY"> BIRTHDAY </label>            <input type="date" class="form-control" value="<?= $user->BIRTHDAY ?>" name="BIRTHDAY" id="BIRTHDAY"                   required >        </div>        <!--        // CITY COUNTRY REGION STREET-->        <div class="form-group">            <label for="CITY"> CITY </label>            <input type="text" class="form-control" value="<?= $user->ADDRESS->CITY ?>" name="CITY" id="CITY"                   required >        </div>        <div class="form-group">            <label for="COUNTRY"> COUNTRY </label>            <input type="text" class="form-control" value="<?= $user->ADDRESS->COUNTRY ?>" name="COUNTRY" id="COUNTRY" required >        </div>        <div class="form-group">            <label for="REGION"> REGION </label>            <input type="text" class="form-control" value="<?= $user->ADDRESS->REGION ?>" name="REGION" id="REGION" required >        </div>        <div class="form-group">            <label for="STREET"> STREET </label>            <input type="text" class="form-control" value="<?= $user->ADDRESS->STREET ?>" name="STREET" id="STREET" required >        </div>        <button name="submit" type="submit" class="btn btn-primary">Submit</button>    </form></div>